import React from 'react';
import Link from 'next/link';
import ImageC from './ImageC';
import styled from 'styled-components';
import { getStrapiMedia } from '../actions/media';
const Card = ({ article }) => {
	return (
		<Link href={`/article/${article.slug}`} passHref>
			<a>
				<Container
					style={{
						background: `url(${getStrapiMedia(
							article.image
						)}) center / cover no-repeat`,
					}}
				>
					{/* <div className="img-container">
						<ImageC image={article.image} width={400} height={400} />
					</div> */}
					<div className="body">
						<p className="category">{article.category.name}</p>
						<p className="title">{article.title}</p>

						<div className="card-info">{article.description}</div>
					</div>
				</Container>
			</a>
		</Link>
	);
};

const Container = styled.div`
	height: 400px;
	overflow: hidden;
	text-align: center;
	margin: 0.6em;
	transition: all 0.5s ease-in-out;
	position: relative;
	.card-info {
		width: 100%;
		position: absolute;
		bottom: 100px;
		left: 0;
		margin: 0 auto;
		padding: 0 50px;
		color: $txt-color;
		font-family: 'Droid Serif', serif;
		font-style: 16px;
		line-height: 24px;
		z-index: 20;
		opacity: 0;
		transition: bottom 0.3s, opacity 0.3s cubic-bezier(0.33, 0.66, 0.66, 1);
	}
	&:hover {
		.card-info {
			opacity: 1;
			bottom: 120px;
		}
		box-shadow: 4px 8px 10px rgba(0, 0, 0, 0.5);
	}
`;

export default Card;
