import React, { useState, useEffect } from 'react';
import styled from 'styled-components';
// import { useSpring, animated, config } from 'react-spring';
import Link from 'next/link';
import Brand from './Brand';
import BurgerMenu from './BurgerMenu';
import CollapseMenu from './CollapseMenu';
import { menuItems } from '../../helpers/navLinks';
// import { listSearch } from '../../actions/product';
// import KeyboardArrowRightIcon from '@material-ui/icons/KeyboardArrowRight';
// import KeyboardArrowDownIcon from '@material-ui/icons/KeyboardArrowDown';
// import SearchIcon from '@material-ui/icons/Search';
// import { theme } from '../theme/theme';
// import { useRouter } from 'next/router';

const Navbar = (props) => {
	const Links = () => {
		if (menuItems.length > 0) {
			return menuItems.map((menuItem, i) => {
				return (
					<Link href={menuItem.link} key={i}>
						<a className="menu-item">{menuItem.label}</a>
					</Link>
				);
			});
		}
	};

	return (
		<>
			<NavBar>
				<Brand />

				<div className="link-container ">{Links()}</div>

				<BurgerWrapper>
					<BurgerMenu
						navbarState={props.navbarState}
						handleNavbar={props.handleNavbar}
					/>
				</BurgerWrapper>
			</NavBar>
			<CollapseMenu
				navbarState={props.navbarState}
				handleNavbar={props.handleNavbar}
				topNavbarLinks={props.topNavbarLinks}
				categoryNavLinks={props.categoryNavLinks}
			/>
			{/* {searched && (
                <ProductDisplay className="bg-white w-100 pb-2">
                    {searchedProducts(searchResults)}
                </ProductDisplay>
            )} */}
		</>
	);
};

export default Navbar;

const NavBar = styled.nav`
	width: 100%;
	padding: 0.6em;
	z-index: 1;
	font-size: 1em;
	display: flex;
	justify-content: space-between;
	.link-container {
		padding: 0.6em 0;
		.menu-item {
			padding: 0.6em;
			text-decoration: none;
			color: #000;
			font-weight: 700;
		}
		@media (max-width: 768px) {
			display: none;
		}
	}
`;

const BurgerWrapper = styled.div`
	@media (min-width: 768px) {
		display: none;
	}
`;
