import Layout from '../components/Layout';
import styles from '../styles/Home.module.css';
import Articles from '../components/Articles';
import Seo from '../components/Seo';
import ImageC from '../components/ImageC';
import fetchApi from '../actions/api';
const Home = ({ articles, categories, homePage }) => {
	return (
		<Layout categories={categories}>
			<Seo seo={homePage.seo} />
			<div className="hero-banner">
				<ImageC image={homePage.shareIamge} width={1920} height={400} />
			</div>
			<div className="title">
				<h1>{homePage.hero.title}</h1>
			</div>
			<div className="content">
				<Articles articles={articles} />
			</div>
		</Layout>
	);
};

export async function getServerSideProps(context) {
	const articles = fetchApi('/articles?status=published');
	const categories = fetchApi('/categories');
	const homePage = fetchApi('/homepage');

	if (articles && categories && homePage) {
		return {
			props: {
				articles,
				categories,
				homePage,
			},
		};
	} else {
		context.res.statusCode = 404;
		return {
			props: {},
		};
	}
}
export default Home;
