import React, { useState } from 'react';
import styled from 'styled-components';
import Link from 'next/link';
import { useSpring, animated } from 'react-spring';
// import { signout, isAuth } from '../../actions/auth';
// import PersonIcon from '@material-ui/icons/Person';
// import PersonAddIcon from '@material-ui/icons/PersonAdd';
// import ExitToAppIcon from '@material-ui/icons/ExitToApp';
// import KeyboardArrowRightIcon from '@material-ui/icons/KeyboardArrowRight';
// import KeyboardArrowDownIcon from '@material-ui/icons/KeyboardArrowDown';
import { menuItems } from '../../helpers/navLinks';
import { theme } from '../theme/theme';
const CollapseMenu = (props) => {
	const { open } = useSpring({ open: props.navbarState ? 0 : 1 });

	const Links = () => {
		if (menuItems.length > 0) {
			return menuItems.map((menuItem, i) => {
				return (
					<Link href={menuItem.link} key={i}>
						<a className="menu-item">{menuItem.label}</a>
					</Link>
				);
			});
		}
	};
	if (props.navbarState === true) {
		return (
			<React.Fragment>
				<Closer onClick={props.handleNavbar} />
				<CollapseWrapper
					style={{
						transform: open
							.interpolate({
								range: [0, 0.2, 0.3, 1],
								output: [0, 0, 0, -200],
							})
							.interpolate((openValue) => `translate3d( ${openValue}px,0,0)`),
					}}
				>
					<NavLinks>{Links()}</NavLinks>
				</CollapseWrapper>
			</React.Fragment>
		);
	}
	return null;
};

export default CollapseMenu;

const CollapseWrapper = styled(animated.div)`
	background: #fff;
	position: absolute;
	z-index: 999;
	top: 0;
	bottom: 0;
	width: 300px;
	form {
		z-index: 2;
		box-shadow: 0 -1px 9px rgba(0, 0, 0, 0.17);
		display: flex;
		height: 80px;
		padding: 2px;
		background: #fff;
		min-width: 120px !important;
		border-radius: 2px;
		border: 1px solid rgba(155, 155, 155, 0.2);
		input {
			width: 100%;
			height: 100%;
			padding: 0 10px;
			color: #fff;
			font-size: 17px;
			border: none;
			font-weight: 500;
			background: none;
			&:focus {
				outline: none;
				border-color: inherit;
				-webkit-box-shadow: none;
				box-shadow: none;
			}
		}
		button {
			padding: 0 15px;
			color: #fff;
			font-size: 17px;
			background: #fff;
			border: none;
			border-radius: 2px;
			cursor: pointer;
		}
	}
	.mobile-nav-tabs {
		border-bottom: 1px solid rgba(129, 129, 129, 0.2);
		background-color: rgba(0, 0, 0, 0.04);

		ul {
			display: flex;
			align-items: stretch;
			flex-direction: row;
			list-style-type: none;
			margin-bottom: 0;

			li {
				position: relative;
				flex: 1 0 50%;
				padding: 18px 15px;
				max-width: 50%;
				width: 50%;
				color: #909090;
				text-align: center;
				text-transform: uppercase;
				letter-spacing: 0.3px;
				font-weight: 900;
				font-size: 14px;
				// line-height: 1.3;
				cursor: pointer;
				transition: background-color 0.25s ease, color 0.25s ease;
			}
			.active {
				background-color: rgba(0, 0, 0, 0.05);
				color: #333;
				box-shadow: 0 1px 0px rgba(32, 122, 189, 0.5);
			}
		}
	}
`;
const Closer = styled.div`
	position: fixed;
	z-index: 998;
	top: 0;
	bottom: 0;
	width: 100%;
	height: 100%;
	right: 0;
	left: 0;
	opacity: 1;
	visibility: visible;
	background-color: rgba(0, 0, 0, 0.7);
	transition: opacity 0.25s ease, visibility 0s ease;
`;

const NavLinks = styled.ul`
	list-style-type: none;
	// padding: 2rem 1rem 2rem 2rem;
	border-top: 1px solid rgba(129, 129, 129, 0.2);
	height: 100%;
	background: #fff;
	& li {
		transition: all 300ms linear 0s;
		position: relative;
		display: block;
		background: #fff;
		.list-sub-menu {
			// background: rgba(${theme.colorsRGB.primary}, 1);
			a {
				color: #fff;
			}
		}
		.icon-sub-menu {
			position: absolute;
			top: 0;
			right: 0;
			padding-right: 0;
			width: 50px;
			height: 50px;
			border-left: 1px solid rgba(129, 129, 129, 0.2);
			color: #2d2a2a;
			text-align: center;
			line-height: 50px;
			cursor: pointer;
			transition: background-color 0.25s ease, color 0.25s ease;
			backface-visibility: hidden;
			-webkit-backface-visibility: hidden;
		}
		.active-sub-menu {
			// background: ${theme.colors.primary};
			color: #fff;
		}
	}

	& a {
		// font-size: 1em;
		// line-height: 2;
		// color: #000;
		// text-transform: uppercase;
		// text-decoration: none;
		// cursor: pointer;

		display: flex;
		align-items: center;
		flex-direction: row;
		padding-top: 5px;
		padding-right: 20px;
		padding-bottom: 5px;
		padding-left: 20px;
		min-height: 50px;
		border-bottom: 1px solid rgba(129, 129, 129, 0.2);
		color: #2d2a2a;
		letter-spacing: 0.2px;
		font-weight: 600;
		font-size: 14px;
		line-height: 1.3;

		&:hover {
			color: #207abd;
			border-bottom: 1px solid #207abd;
		}
	}
`;
