import styled from 'styled-components';
const Footer = () => {
	return (
		<Wrapper>
			<div class="copyright">
				<p>Â© 2021 - Organisation</p>
			</div>
			<div class="social">
				<a href="#" class="support">
					Contact Us
				</a>
			</div>
		</Wrapper>
	);
};

const Wrapper = styled.footer`
	width: 100%;
	display: flex;
	margin: 2em 0;
	justify-content: space-between;
	background-color: #333;
	padding: 0.6em;
	align-items: center;
	.copyright {
		p {
			color: white;
			font-size: 0.7em;
		}
	}
	.social {
		a {
			text-decoration: none;
			color: white;
			font-weight: bold;
			font-size: 0.7em;
			&:hover {
				color: #207abd;
				border-bottom: 1px solid #207abd;
			}
		}
	}
`;

export default Footer;
