import Layout from '../components/Layout';
import styled from 'styled-components';
import Articles from '../components/Articles';
import Seo from '../components/Seo';
import ImageC from '../components/ImageC';
import { fetchAPI } from '../actions/api';
import { getStrapiMedia } from '../actions/media';
const Home = ({ articles, homePage }) => {
	return (
		<Layout>
			<Seo seo={homePage.seo} />
			<div className="content" style={{ margin: '0 0.6em' }}>
				<Hero
					style={{
						background: `url(${getStrapiMedia(
							homePage.hero.heroImage
						)}) center / cover no-repeat`,
					}}
				>
					<h1>{homePage.hero.title}</h1>
				</Hero>

				<Articles articles={articles} />
			</div>
		</Layout>
	);
};

const Hero = styled.div`
	postion: relative;
	text-align: center;
	overflow: hidden;
	height: 400px;
	width: 100%;

	h1 {
		position: absolute;
		top: 50%;
		left: 50%;
		transform: translate(-50%, -50%);
		color: #fff;
	}
`;
export async function getServerSideProps(context) {
	const articles = await fetchAPI('/articles?status=published');
	const homePage = await fetchAPI('/homepage');

	if (articles) {
		return {
			props: {
				articles,
				homePage,
			},
		};
	} else {
		context.res.statusCode = 404;
		return {
			props: {},
		};
	}
}

export default Home;
