import Layout from '../components/Layout';
import styled from 'styled-components';
import Articles from '../components/Articles';
import Seo from '../components/Seo';
import ImageC from '../components/ImageC';
import { fetchAPI } from '../actions/api';
const Home = ({ articles, homePage }) => {
	return (
		<Layout>
			<Seo seo={homePage.seo} />
			<Hero
				style={{
					background: `url(${homePage.hero.heroImag}) center / cover no-repeat`,
				}}
			>
				{/* <ImageC image={homePage.hero.heroImage} width={1280} height={400} /> */}
				<h1>{homePage.hero.title}</h1>
			</Hero>

			<div className="content">
				<Articles articles={articles} />
			</div>
		</Layout>
	);
};

const Hero = styled.div`
	postion: relative;
	text-align: center;
	overflow: hidden;
	height: 400px;
	width: 1280;
	margin: 0 0.6em;
	h1 {
		position: absolute;
		top: 50%;
		left: 50%;
		transform: translate(-50%, -50%);
		color: #fff;
	}
`;
export async function getServerSideProps(context) {
	const articles = await fetchAPI('/articles?status=published');
	const homePage = await fetchAPI('/homepage');

	if (articles) {
		return {
			props: {
				articles,
				homePage,
			},
		};
	} else {
		context.res.statusCode = 404;
		return {
			props: {},
		};
	}
}

export default Home;
