import React from 'react';
import styled from 'styled-components';
const Footer = () => {
	return (
		<Wrapper>
			<div class="copyright">
				<p>Â© 2021 - Organisation</p>
			</div>
			<div class="social">
				<a href="#" class="support">
					Contact Us
				</a>
			</div>
		</Wrapper>
	);
};

const Wrapper = styled.footer`
	width: 100%;
	display: flex;
	margin: 2em 0;
	justify-content: space-between;
	background-color: #333;
	padding: 0.6em;
	.copyright {
		@media (max-width: 600px) {
			width: 100%;
		}
		p {
			color: white;
			font-size: 0.7em;
			@media (max-width: 600px) {
				text-align: center;
				padding: 0;
			}
		}
	}
	.social {
		@media (max-width: 600px) {
			width: 100%;
		}
		a {
			text-decoration: none;
			color: white;
			text-align: center;
			font-weight: bold;
			font-size: 0.7em;
			&:hover {
				background-color: #222;
			}
		}
	}
`;

export default Footer;
