import Layout from '../../components/Layout';
import styled from 'styled-components';
import Seo from '../../components/Seo';
import { fetchAPI } from '../../actions/api';
import { getStrapiMedia } from '../../actions/media';
import HeroImage from '../../components/HeroImage';
import ImageC from '../../components/ImageC';
import Error from 'next/error';
import Moment from 'react-moment';
import ReactMarkdown from 'react-markdown';
const Article = ({ article }) => {
	const seo = {
		metaTitle: article.title,
		metaDescription: article.description,
		shareImage: article.image,
		article: true,
	};

	if (!article) {
		return <Error statusCode={404} />;
	} else {
		return (
			<Layout>
				<Seo seo={seo} />
				<div className="content">
					<HeroImage title={article.title} image={article.image} />
					<ReactMarkdown
						source={article.content}
						escapeHtml={false}
						transformImageUri={(uri) =>
							uri.startsWith('http') ? uri : getStrapiMedia(uri)
						}
					/>
					<div className="uk-section">
						<div className="uk-container uk-container-small">
							<hr className="uk-divider-small" />
							<div className="uk-grid-small uk-flex-left" data-uk-grid="true">
								<div>
									{article.author.picture && (
										<ImageC
											image={article.author.picture}
											height={30}
											width={30}
										/>
									)}
								</div>
								<div className="uk-width-expand">
									<p className="uk-margin-remove-bottom">
										By {article.author.name}
									</p>
									<p className="uk-text-meta uk-margin-remove-top">
										<Moment format="MMM Do YYYY">{article.published_at}</Moment>
									</p>
								</div>
							</div>
						</div>
					</div>
				</div>
			</Layout>
		);
	}
};

export async function getServerSideProps(context) {
	const article = await fetchAPI(
		`/articles?slug=${context.query.slug}&status=published`
	);

	if (article) {
		return {
			props: {
				article: article[0],
			},
		};
	} else {
		context.res.statusCode = 404;
		return {
			props: {},
		};
	}
}

export default Article;
