	z-index: 998;
	top: 0;
	bottom: 0;
	width: 100%;
	height: 100%;
	right: 0;
	left: 0;
	opacity: 1;
	visibility: visible;
	background-color: rgba(0, 0, 0, 0.7);
	transition: opacity 0.25s ease, visibility 0s ease;
`;

