import React from 'react';
import Link from 'next/link';
import ImageC from './ImageC';
import styled from 'styled-components';
import { getStrapiMedia } from '../actions/media';
const Card = ({ article }) => {
	return (
		<Link href={`/article/${article.slug}`} passHref>
			<a>
				<Container
					style={{
						background: `url(${getStrapiMedia(
							article.image
						)}) center / cover no-repeat`,
					}}
				>
					{/* <div className="img-container">
						<ImageC image={article.image} width={400} height={400} />
					</div> */}
					<div className="body">
						<p className="category">{article.category.name}</p>
						<p className="title">{article.title}</p>
					</div>
					<div class="gradient-overlay"></div>
					<div class="color-overlay"></div>
				</Container>
			</a>
		</Link>
	);
};

const Container = styled.div`
	height: 400px;
	overflow: hidden;
	box-shadow: 3px 3px 20px rgba(0, 0, 0, 0.5);
	text-align: center;
	margin: 0.6em;
	position: relative;
	.color-overlay {
		/* Rectangle 11: */
		// background: rgba(84, 104, 110, 0);
		width: 100%;
		height: 600px;
		position: absolute;
		z-index: 10;
		top: 0;
		left: 0;
		transition: background 0.3s cubic-bezier(0.33, 0.66, 0.66, 1);
	}
	.gradient-overlay {
		/* bg-gradient: */
		background-image: -webkit-linear-gradient(
			rgba(0, 0, 0, 0) 0%,
			rgba(0, 0, 0, 0.6) 21%
		);
		background-image: -moz-linear-gradient(
			rgba(0, 0, 0, 0) 0%,
			rgba(0, 0, 0, 0.6) 21%
		);
		background-image: -o-linear-gradient(
			rgba(0, 0, 0, 0) 0%,
			rgba(0, 0, 0, 0.6) 21%
		);
		background-image: linear-gradient(
			rgba(0, 0, 0, 0) 0%,
			rgba(0, 0, 0, 0.6) 21%
		);
		width: 100%;
		height: 600px;
		position: absolute;
		top: 350px;
		left: 0;
		z-index: 15;
	}

	&:hover {
		.card-info {
			opacity: 1;
			bottom: 120px;
		}
		.color-overlay {
			background: rgba(84, 104, 110, 0.2);
		}
	}
`;

export default Card;
