import React from 'react';
import styled from 'styled-components';
const Footer = () => {
	return (
		<Wrapper>
			<div class="copyright">
				<p>Â© 2021 - Organisation</p>
			</div>
			<div class="social">
				<a href="#" class="support">
					Contact Us
				</a>
				<a href="#" class="face">
					f
				</a>
				<a href="#" class="tweet">
					t
				</a>
				<a href="#" class="linked">
					in
				</a>
			</div>
		</Wrapper>
	);
};

const Wrapper = styled.footer`
	footer {
		width: 100%;
		display: inline-block;
		margin: 2em 0;
		height: 70px;
		background-color: #999;
		.copyright {
			width: 50%;
			float: left;
			@media (max-width: 600px) {
				width: 100%;
			}
			p {
				padding-left: 10%;
				color: white;
				font-size: 0.7em;
				line-height: 70px;
				text-transform: capitalize;
				letter-spacing: 1px;
				@media (max-width: 600px) {
					text-align: center;
					padding: 0;
				}
			}
		}
		.social {
			width: 50%;
			float: right;
			@media (max-width: 600px) {
				width: 100%;
			}
			a {
				float: left;
				line-height: 70px;
				text-decoration: none;
				color: white;
				text-align: center;
				font-weight: bold;
				@include transition(all, 0.3s);
				&:hover {
					background-color: #222;
					@include transition(all, 0.3s);
				}
			}
		}
		.linked {
			background-color: $dk-blue;
			font-size: $social-size;
			width: $social-width;
		}

		.face {
			background-color: $blue;
			font-size: $social-size;
			width: $social-width;
		}

		.tweet {
			background-color: $light-blue;
			font-size: $social-size;
			width: $social-width;
		}

		.support {
			background-color: $green;
			font-size: 0.8em;
			width: 40%;
		}
	}
`;

export default Footer;
