import React from 'react';
import styled from 'styled-components';
const Footer = () => {
	return (
		<Wrapper>
			<div class="copyright">
				<p>Â© 2021 - Organisation</p>
			</div>
			<div class="social">
				<a href="#" class="support">
					Contact Us
				</a>
			</div>
		</Wrapper>
	);
};

const Wrapper = styled.footer`
	width: 100%;
	display: flex;
	margin: 2em 0;
	justify-content: space-between;
	background-color: #333;
	padding: 0.6em .copyright {
		@media (max-width: 600px) {
			width: 100%;
		}
		p {
			color: white;
			font-size: 0.7em;
			text-transform: capitalize;
			letter-spacing: 1px;
			@media (max-width: 600px) {
				text-align: center;
				padding: 0;
			}
		}
	}
	.social {
		@media (max-width: 600px) {
			width: 100%;
		}
		a {
			line-height: 50px;
			text-decoration: none;
			color: white;
			text-align: center;
			font-weight: bold;
			@include transition(all, 0.3s);
			&:hover {
				background-color: #222;
				@include transition(all, 0.3s);
			}
		}
	}

	.support {
		background-color: #01a25a;
		font-size: 0.8em;
		width: 40%;
	}
`;

export default Footer;
